//Back-end complete function Template for C



int det(int m, int n, int B[][n])
{

    int row_size = m;
    int column_size = n;

    if (row_size != column_size) {
        printf("DimensionError: Operation Not Permitted \n");
        return -1;
    }

    //base cases
    else if (row_size == 1)
        return (B[0][0]);

    else if (row_size == 2)
        return (B[0][0]*B[1][1] - B[1][0]*B[0][1]);

    else 
    {
        int minor[row_size-1][column_size-1];
        int row_minor, column_minor;
        int firstrow_columnindex;
        int sum = 0;

        register int row,column;

        //excluding first row and current column.
        for(firstrow_columnindex = 0; firstrow_columnindex < row_size; firstrow_columnindex++) 
        {
            row_minor = 0;

            for(row = 1; row < row_size; row++)
            {
                column_minor = 0;

                for(column = 0; column < column_size; column++) 
                {
                    if (column == firstrow_columnindex)
                        continue;
                    else
                        minor[row_minor][column_minor] = B[row][column];

                    column_minor++;
                }

                row_minor++;
            }

            m = row_minor;
            n = column_minor;

            //adding or subtracting the value of (B[0][i])*Cofactor alternately.
            if (firstrow_columnindex % 2 == 0)
                sum += B[0][firstrow_columnindex] * det(m, n, minor);
            else
                sum -= B[0][firstrow_columnindex] * det(m, n, minor);

        }
        //returning the determinant.
        return sum;
    }
}

//Function for finding determinant of matrix.
int determinantOfMatrix(int n, int matrix[][n])
{
    return det(n, n, matrix);
}

class Solution
{   
    public:
    //Function to get cofactor of matrix[p][q] in temp[][].
    void getCofactor(vector<vector<int>> & matrix,vector<vector<int>>&temp,int p,int q,int n)
    {
        int i = 0, j = 0;
        for (  int row = 0; row < n; row++)
        {
            for (  int col = 0; col < n; col++)
            {
                //copying only those elements into temporary matrix 
                //which are not in given row and column.
                if (row != p && col != q)
                {
                    temp[i][j++] = matrix[row][col];

                    //if row is filled, we increase row index and
                    //reset column index.
                    if (j == n - 1)
                    {
                        j = 0;
                        i++;
                    }
                }
            }
        }
    }


    //Function for finding determinant of matrix.
    int determinantOfMatrix(vector<vector<int> > matrix, int n)
    {
        int D = 0; 

        //base case
        if (n == 1)
            return matrix[0][0];

        //creating a list to store Cofactors.
        vector<vector<int> > temp(n, vector<int>(n)); 
        int sign = 1; 

        //iterating for each element of first row.
        for (  int i = 0; i < n; i++)
        {
            //getting Cofactor of matrix[0][i].
            getCofactor(matrix, temp, 0, i, n);
            D += sign * matrix[0][i] * determinantOfMatrix(temp, n - 1);

            //terms are to be added with alternate sign so changing the sign.
            sign = -sign;
        }
        //returning the determinant.
        return D;
    }
};

