class Solution
{
    //Function to get cofactor of matrix[p][q] in temp[][]
    public void getCofactor(List<List<int>> matrix, List<List<int>> temp, int p, int q, int n)
    {
        int i = 0, j = 0;
        for (int row = 0; row < n; row++)
        {
            for (int col = 0; col < n; col++)
            {
                //copying only those elements into temporary matrix 
                //which are not in given row and column.
                if (row != p && col != q)
                {
                    temp[i][j++] = matrix[row][col];

                    //if row is filled, we increase row index and
                    //reset column index.
                    if (j == n - 1)
                    {
                        j = 0;
                        i++;
                    }
                }
            }
        }
    }


    //Function for finding determinant of matrix.
    public int determinantOfMatrix(List<List<int>> matrix, int n)
    {
        int D = 0;

        //base case
        if (n == 1)
            return matrix[0][0];

        //creating a list to store Cofactors.
        List<List<int>> temp = new List<List<int>>();
        for (int i = 0; i < n; i++)
        {
            temp.Add(new List<int>());
            for (int j = 0; j < n; j++)
            {
                temp[i].Add(0);
            }
        }
        int sign = 1;

        //iterating for each element of first row.
        for (int i = 0; i < n; i++)
        {
            //getting Cofactor of matrix[0][i].
            getCofactor(matrix, temp, 0, i, n);
            D += sign * matrix[0][i] * determinantOfMatrix(temp, n - 1);

            //terms are to be added with alternate sign so changing the sign.
            sign = -sign;
        }
        //returning the determinant.
        return D;
    }
}

//Back-end complete function Template for javascript

/**
 * @param {number[][]} matrix
 * @param {number} n
 * @returns {number}
*/
class Solution 
{
    //Function to get cofactor of matrix[p][q] in temp[][].
    getCofactor(matrix, temp, p, q, n)
    {
        let i = 0, j = 0;
        for (let row = 0; row < n; row++)
        {
            for (let col = 0; col < n; col++)
            {
                //copying only those elements into temporary matrix 
                //which are not in given row and column.
                if (row != p && col != q)
                {
                    temp[i][j++] = matrix[row][col];

                    //if row is filled, we increase row index and
                    //reset column index.
                    if (j == n - 1)
                    {
                        j = 0;
                        i++;
                    }
                }
            }
        }
    }


    //Function for finding determinant of matrix.
    determinantOfMatrix(matrix, n)
    {
        let D = 0; 

        //base case
        if (n == 1)
            return matrix[0][0];

        //creating a list to store Cofactors.
        let temp = new Array(n);
        for(let i=0;i<n;i++)
        {
            temp[i] = new Array(n);
            temp[i].fill(0);
        }
        let sign = 1; 

        //iterating for each element of first row.
        for (let i = 0; i < n; i++)
        {
            //getting Cofactor of matrix[0][i].
            this.getCofactor(matrix, temp, 0, i, n);
            D += sign * matrix[0][i] * this.determinantOfMatrix(temp, n - 1);

            //terms are to be added with alternate sign so changing the sign.
            sign = -sign;
        }
        //returning the determinant.
        return D;
    }
}
