class Solution{
	public:
	// Function to find the focal length given radius and type
	int findFocalLength(float R, string type){
		// Calculate the distance from the center to the lens
		float d = (R / 2.0);
		
		// If the lens is convex, multiply distance by -1
		if(type == "convex")
			d *= -1;
		
		// Round the distance down to the nearest integer
		int f = floor(d);
		
		// Return the focal length
		return f;
	}
};
