//Back-end complete function Template for C++

class Solution {
  public:
    // Function to find sum of diagonals
    int diagonalSum(int arr[4][4]) {
        int sum = 0;
        for (int i = 0; i < 4; i++) {
            sum += arr[i][i] + arr[3 - i][i];
        }
        return sum;
    }
};
