//Back-end complete function template in C++

class Solution
{
public:
    //Function to calculate the difference series of a given number N.
    int differenceSeries(int N)
    {
        //Calculating the difference series using the provided formula.
        int ans = (2*N*(N+1)*(2*N+1)/3)-(N*(2*N+1)*(2*N-1)/3);

        //Returning the answer.
        return ans;
    }
};

