//Back-end complete function template in C++

class Solution {
  public:
    //Function to check if the given number contains digits in increasing or decreasing order.
    int difProblem(string N) {

        int f1 = 1, f2 = 1;
        int temp2=9;
        //iterating over the string from the end
        for(int i = N.length()-1 ; i>=0 ; i--)
        {
            //checking if the current digit is greater than the previous one
            if(N[i]-'0' > temp2)
                f1 = 0;

            temp2 = N[i]-'0';
        }

        temp2=0;

        //iterating over the string from the end
        for(int i = N.length()-1 ; i>=0 ; i--)
        {
            //checking if the current digit is smaller than the previous one
            if(N[i]-'0' < temp2)
                f2 = 0;

            temp2 = N[i]-'0';
        }

        //checking if the number contains digits in increasing or decreasing order
        if(f1==0 && f2==0)
            return 0;
        else
            return 1;
    }
};
