//Back-end complete function Template for Java

class Solution {
    public static ArrayList<Integer> uniqueSorted(int arr[]) {
        // Declaring and Intializing a new TreeSet
        TreeSet<Integer> s = new TreeSet<>();

        // adding elements from the
        // ArrayList to the TreeSet
        for (int i : arr) s.add(i);

        // Declaring and Initializing a new ArrayList
        ArrayList<Integer> ans = new ArrayList<Integer>();

        // adding elements from the
        // TreeSet to the ArrayList
        // which will automatically
        // be in sorted non-decreasing order
        for (int i : s) ans.add(i);

        // returing the ArrayList
        return ans;
    }
}
