class Solution {
  public:
    //Function to determine the winner of the game.
    string divAndSub(int N) {

        //if N is 1, Arya wins.
        if(N == 1)
            return "Arya";

        //creating an array with size N+1 and initializing all elements to 0.
        int a[N+1] = {0};

        //assigning 1 to first 5 elements.
        for(int i=1; i<=5; i++)
            a[i] = 1;

        //iterating through 6 to N.
        for(int i=6; i<=N; i++)
        {
            //checking if division by 2 is possible.
            if (a[i/2] == 0)
                a[i] = 1;
            //checking if subtraction by 2 is possible.
            else if (a[i-2] == 0)
                a[i] = 1;

            //checking if division by 3 is possible.
            if (a[i/3] == 0)
                a[i] = 1;
            //checking if subtraction by 3 is possible.
            else if (a[i-3] == 0)
                a[i] = 1;

            //checking if division by 4 is possible.
            if (a[i/4] == 0)
                a[i] = 1;
            //checking if subtraction by 4 is possible.
            else if (a[i-4] == 0)
                a[i] = 1;

            //checking if division by 5 is possible.
            if (a[i/5] == 0)
                a[i] = 1;
            //checking if subtraction by 5 is possible.
            else if (a[i-5] == 0)
                a[i] = 1;
        }

        //if a[N] is 1, Jon wins, else Arya wins.
        if (a[N] == 1)
            return "Jon";
        else
            return "Arya";
    }
};

