//Back-end complete function template in C++

class Solution {
public:
	//Function to calculate the least common multiple of two numbers.
	long long int lcm(long long int a, long long int b){
		return a*(b/__gcd(a, b));
	}

	//Function to count the number of integers divisible by k among subsets of given numbers.
    long long int Count(vector<int>nums, long long int k) {
    	long long int ans = 0;
    	int n = nums.size();

    	//iterating over all possible subsets of given numbers.
    	for(int i = 0; i < (1 << n); i++){
    		long long int x = 1;
    		int cnt = 0;

    		//iterating over all numbers in the subset.
    		for(int j = 0; j < n; j++){
    			//checking if j-th number is included in the subset.
    			if(i & (1 << j)){
    				cnt++;

    				//calculating the least common multiple if x is less than or equal to k.
    				if(x <= k){
    					x = lcm(x, nums[j]);
    				}
    			}
    		}

    		//if no numbers are included in the subset, continue to the next iteration.
    		if(!cnt) continue;

    		//if the count of numbers in the subset is odd, increment the answer by k divided by x.
    		if(cnt&1)
    			ans += (k/x);
    		//if the count of numbers in the subset is even, decrement the answer by k divided by x.
    		else
    			ans -=(k/x);
    	}

    	//returning the final answer.
    	return ans;
    }
};
