// Function to convert binary string to equivalent base 4 number
int equivalentBase4(string bin) {
    if (bin.compare("00") == 0)  // if binary string is "00", equivalent base 4 number is 0
        return 0;
    if (bin.compare("01") == 0)  // if binary string is "01", equivalent base 4 number is 1
        return 1;
    if (bin.compare("10") == 0)  // if binary string is "10", equivalent base 4 number is 2
        return 2;
    return 3;  // if binary string is "11", equivalent base 4 number is 3
}

// Function to check if the binary string is divisible by 5
string isDivisibleBy5(string bin) {
    int l = bin.size();

    if (l % 2 != 0)  // if binary string length is odd, add '0' at the beginning
        bin = '0' + bin;

    int odd_sum = 0, even_sum = 0;
    int isOddDigit = 1;

    // Iterate through the binary string and calculate the sum of digits at odd and even positions
    for (int i = 0; i < bin.size(); i += 2) {
        if (isOddDigit)
            odd_sum += equivalentBase4(bin.substr(i, 2));  // add equivalent base 4 number of odd digits to odd_sum
        else
            even_sum += equivalentBase4(bin.substr(i, 2));  // add equivalent base 4 number of even digits to even_sum

        isOddDigit ^= 1;  // toggle isOddDigit for next iteration
    }

    if (abs(odd_sum - even_sum) % 5 == 0)  // check if the difference between odd_sum and even_sum is divisible by 5
        return "Yes";

    return "No";  // if not divisible by 5, return "No"
}

