// Back-end complete function Template for C++

class Solution{
public:
    // Function to calculate the waiting time
    int waitingTime(int N, int X){
        // If there are more than or equal to 10 servers,
        // no waiting time
        if(X >= 10)
            return 0;
        
        // Calculate the waiting time based on the number of 
        // servers and their efficiency
        return ((N - 1)*(10 - X));
    }
};

//Back-end complete function Template for Java

class Solution{
    // Function to calculate the waiting time
    static int waitingTime(int N, int X){
        // If the number of students is less than or equal to the number of available computers
        if(X >= 10)
            // No waiting time is required
            return 0;

        // Calculating the waiting time
        return ((N - 1)*(10 - X));
    }
}

