string isCircular(string path){
    int x = 0, y = 0; //initialize x and y coordinates
    int dir = 0; //initialize direction as 0 (North)

  for (int i=0; i < path.length(); i++)  { 
      char move = path[i]; //get the current move

      //update direction based on the move
      if (move == 'R') 
        dir = (dir + 1)%4; 
      else if (move == 'L') 
        dir = (4 + dir - 1)%4; 

      //update x and y coordinates based on the direction
      else { 
         if (dir == 0) 
            y++; 
         else if (dir == 1) 
            x++; 
         else if (dir == 2) 
            y--; 
         else 
            x--; 
      } 
  } 

    //check if the final coordinates are (0,0) and return the result
    if (x == 0 && y == 0) return "Circular";
    else return "Not Circular";
}

class Solution
{
    public string isCircular(string path)
    {
        int x = 0, y = 0;
        int dir = 0;

        for (int i = 0; i < path.Length; i++)
        {
            char move = path[i];

            if (move == 'R')
                dir = (dir + 1) % 4;
            else if (move == 'L')
                dir = (4 + dir - 1) % 4;
            else
            {
                if (dir == 0)
                    y++;
                else if (dir == 1)
                    x++;
                else if (dir == 2)
                    y--;
                else
                    x--;
            }
        }

        if (x == 0 && y == 0)
            return "Circular";
        else
            return "Not Circular";
    }
}
