class Solution {
    // function to return sum of elements
    // in an array of size n
  public:
    void printTriangle(int n) {
        for (int i = 1; i < n; i++) {
            for (int j = 1; j <= i; j++) {
                cout << "*";
            }
            for (int j = 1; j <= n - i; j++) {
                cout << "  ";
            }
            for (int j = 1; j <= i; j++) {
                cout << "*";
            }
            cout << endl;
        }
        for (int i = n; i >= 1; i--) {
            for (int j = 1; j <= i; j++) {
                cout << "*";
            }
            for (int j = 1; j <= n - i; j++) {
                cout << "  ";
            }
            for (int j = 1; j <= i; j++) {
                cout << "*";
            }
            cout << endl;
        }
    }
};

class Solution {

    // method to print a triangle pattern
    void printTriangle(int n) {
        // loop for printing the upper half of the triangle
        for (int i = 1; i < n; i++) {
            // loop for printing the left half of each row
            for (int j = 1; j <= i; j++) {
                System.out.print("*");
            }
            // loop for printing the space in between the two halves of each row
            for (int j = 1; j <= n - i; j++) {
                System.out.print("  ");
            }
            // loop for printing the right half of each row
            for (int j = 1; j <= i; j++) {
                System.out.print("*");
            }
            System.out.println();
        }

        // loop for printing the lower half of the triangle
        for (int i = n; i >= 1; i--) {
            // loop for printing the left half of each row
            for (int j = 1; j <= i; j++) {
                System.out.print("*");
            }
            // loop for printing the space in between the two halves of each row
            for (int j = 1; j <= n - i; j++) {
                System.out.print("  ");
            }
            // loop for printing the right half of each row
            for (int j = 1; j <= i; j++) {
                System.out.print("*");
            }
            System.out.println();
        }
    }
}

