class Solution:
    def printTriangle(self, n):
        #Printing upper half of the triangle
        for i in range(1, n):
            #Printing asterisks
            for j in range(i):
                print('*', end = '')

            #Printing spaces
            for j in range(1, n - i + 1):
                print('  ', end = '')

            #Printing asterisks
            for j in range(1, i + 1):
                print('*', end = '')

            #Moving to the next line
            print()

        #Printing lower half of the triangle
        for i in range(n, 0, -1):
            #Printing asterisks
            for j in range(1, i + 1):
                print('*', end = '')

            #Printing spaces
            for j in range(1, n - i + 1):
                print('  ', end = '')

            #Printing asterisks
            for j in range(i):
                print('*', end = '')

            #Moving to the next line
            print()

