class Solution {
    // function to return sum of elements
    // in an array of size n
  public:
    void printTriangle(int n) {
        for (int i = 1; i <= n; i++) {
            for (int j = 1; j <= i; j++) {
                cout << j << " ";
            }
            for (int j = n; j > i; j--) {
                cout << "    ";
            }
            for (int j = i; j >= 1; j--) {
                cout << j << " ";
            }
            cout << endl;
        }
    }
};

class Solution {

    // Method to print a triangle with given number of rows
    void printTriangle(int n) {
        // Loop for each row of the triangle
        for (int i = 1; i <= n; i++) {
            // Loop for printing numbers in increasing order
            for (int j = 1; j <= i; j++) {
                System.out.print(j + " ");
            }
            // Loop for printing spaces
            for (int j = n; j > i; j--) {
                System.out.print("    ");
            }
            // Loop for printing numbers in decreasing order
            for (int j = i; j >= 1; j--) {
                System.out.print(j + " ");
            }
            // Printing a new line after each row
            System.out.println();
        }
    }
}

