class Solution {
    //Function to print triangle pattern.
    printTriangle(N)
    {
        for (let i = 1; i <= N; i++) {
            // Print left triangle
            for (let j = 1; j <= i; j++) {
                process.stdout.write(j + " ");
            }
            
            // Print spaces in between
            for (let sp = 1; sp <= 2 * (N - i); sp++) {
                process.stdout.write("  ");
            }
            
            // Print right triangle
            for (let j = i; j >= 1; j--) {
                process.stdout.write(j + " ");
            }
            
            console.log();
        }
    }
}

