class Solution {
    // function to print triangle pattern
  public:
    void printTriangle(int n) {
        
        // loop to print upper part of triangle
        for (int i = n; i >= 1; i--) {
            // loop to print '*' for each row
            for (int j = 1; j <= i; j++) {
                cout << "*";
            }
            // loop to print spaces between '*' for each row
            for (int j = 1; j <= n - i; j++) {
                cout << "  ";
            }
            // loop to print '*' at the end of each row
            for (int j = 1; j <= i; j++) {
                cout << "*";
            }
            // move to the next line
            cout << endl;
        }
        
        // loop to print bottom part of triangle
        for (int i = 1; i <= n; i++) {
            // loop to print '*' for each row
            for (int j = 1; j <= i; j++) {
                cout << "*";
            }
            // loop to print spaces between '*' for each row
            for (int j = 1; j <= n - i; j++) {
                cout << "  ";
            }
            // loop to print '*' at the end of each row
            for (int j = 1; j <= i; j++) {
                cout << "*";
            }
            // move to the next line
            cout << endl;
        }
    }
};

class Solution {

    // Function to print a triangle pattern
    void printTriangle(int n) {
        // Loop to print the upper half of the triangle
        for (int i = n; i >= 1; i--) {
            // Loop to print the stars on the left half of the triangle
            for (int j = 1; j <= i; j++) {
                System.out.print("*");
            }
            // Loop to print the spaces in the middle of the triangle
            for (int j = 1; j <= n - i; j++) {
                System.out.print("  ");
            }
            // Loop to print the stars on the right half of the triangle
            for (int j = 1; j <= i; j++) {
                System.out.print("*");
            }
            System.out.println();
        }
        // Loop to print the lower half of the triangle
        for (int i = 1; i <= n; i++) {
            // Loop to print the stars on the left half of the triangle
            for (int j = 1; j <= i; j++) {
                System.out.print("*");
            }
            // Loop to print the spaces in the middle of the triangle
            for (int j = 1; j <= n - i; j++) {
                System.out.print("  ");
            }
            // Loop to print the stars on the right half of the triangle
            for (int j = 1; j <= i; j++) {
                System.out.print("*");
            }
            System.out.println();
        }
    }
}

