class Solution:

    #Function to print a triangle pattern.
    def printTriangle(self, n):

        #Loop to print upper half of the triangle.
        for i in range(n, 0, -1):
            for j in range(1, i+1):
                print('*', end = '')
            for j in range(n - i):
              print('  ', end = '')
            for j in range(i):
              print('*', end = '')
            print()

        #Loop to print lower half of the triangle.
        for i in range(1, n + 1):
            for j in range(1, i+1):
                print('*', end = '')
            for j in range(n - i):
              print('  ', end = '')
            for j in range(i):
              print('*', end = '')
            print()

