class Solution {
  public:
    long long dyckPaths(int N) {
        
        // Compute value of 2nCn
    	long long res = 1;
    	for (int i = 0 ; i<N ; ++i)
    	{
    		res *= (2*N - i);
    		res /= (i + 1);
    	}
    
    	// return 2nCn/(n+1)
    	long long ans = res/(N+1);
    	
    	return ans;
    }
};

