cpp.full_func:
class Solution {
  public:
    int countElements(vector<int>& arr) {
        int count = 1;
        int maxElement = arr[0];

        for (int i = 1; i < arr.size(); i++) {
            if (arr[i] > maxElement) {
                count++;
                maxElement = arr[i];
            }
        }

        return count;
    }
};

