//Back-end complete function Template for C++
class Solution {
  public:
    vector<int> list_less_than_k(vector<int> &arr, int k) {
        vector<int> result;
        int n = arr.size();
        int i;
        for (i = 0; i < n; i++) {
            if (arr[i] < k) {
                result.push_back(arr[i]);
            }
        }
        return result;
    }
};
