public class Solution {
    // Function to encode the name
    public string encodeTheName(string S) {
        string ans = "";

        // Iterating over each character of the input string
        for(int i = 0; i < S.Length; i++) {
            // Encoding the character and adding it to the answer string
            ans += ((S[i] - 'A') + 65 + i - 10).ToString();
        }

        // Returning the encoded name
        return ans;
