//Back-end complete function Template for C++

class Solution {
  public:
    //Function to convert a given string to Pig Latin.
    string englishWords(string S) {

        //checking if the first letter is a vowel.
        if(S[0] == 'A' || S[0] == 'E' || S[0] == 'I' || S[0] == 'O' || S[0] == 'U')
            return S;

        //variable to store the position of the first vowel.
        int pos = -1;

        //iterating over the string to find the position of the first vowel.
        for(int i=0; i<S.length(); i++)
        {
            if(S[i] == 'A' || S[i] == 'E' || S[i] == 'I' || S[i] == 'O' || S[i] == 'U')
            {
                pos = i;
                break;
            }
        }

        //variable to store the transformed string.
        string ans = "";

        //appending the string from the first vowel position to the end.
        for(int i=pos; i<S.length(); i++)
            ans += S[i];

        //if there is a vowel, appending the string from the beginning 
        //to the first vowel position and adding "AY" at the end.
        if(pos != -1)
        {
            for(int i=0; i<pos; i++)
                ans += S[i];
            ans += "AY";
        }
        //if there is no vowel in the string, appending the whole string
        //and adding "AY" at the end.
        else
        {
            for(int i=0; i<S.length(); i++)
                ans += S[i];
            ans += "AY";
        }

        //returning the transformed string.
        return ans;
    }
};
