class Solution {
  public:
    int countSubarrays(vector<int>& arr) {
        int n = arr.size();
        int count = 0;

        for (int i = 0; i < n; i++) {
            int sum = 0, product = 1;
            for (int j = i; j < n; j++) {
                sum += arr[j];
                product *= arr[j];
                if (sum == product) {
                    count++;
                }
            }
        }

        return count;
    }
};

