class Solution:

    def countSubarrays(self, arr):
        n = len(arr)
        count = 0

        for i in range(n):
            sum_ = 0
            product = 1
            for j in range(i, n):
                sum_ += arr[j]
                product *= arr[j]
                if sum_ == product:
                    count += 1

        return count

