class Solution{
	public:
   	int Evaluate(string str){
   		// getting position of '+'
	    int pluspos = str.find("+");
	    // getting position of equal
	    int equalpos = str.find("=");
	    // finding a
	    int a = atoi(str.substr(pluspos + 1, equalpos - pluspos - 1).c_str());
	    // finding b
	    int b = atoi(str.substr(equalpos + 1).c_str());
	    return (b-a);
    }    
};

//User function Template for C#

class Solution
{
    //Complete this function
    //Function to evaluate a string expression.
    public int Evaluate(string str)
    {
        // getting position of '+'
        int pluspos = str.IndexOf("+");
        // getting position of equal
        int equalpos = str.IndexOf("=");
        // finding a
        int a = int.Parse(str.Substring(pluspos + 1, equalpos - pluspos - 1));
        // finding b
        int b = int.Parse(str.Substring(equalpos + 1));
        return (b - a);
    }
}
