//function to print numbers in the given order
class Geeks {
     static void evenOdd (int a,int b) 
     {
         // checking if 'a' is even
         if(a % 2 == 0)
         {
            // printing 'a' first as it is even
            System.out.println(a);
            // printing 'b' second as it is odd
            System.out.println(b);
         }
         // if 'a' is not even, it must be odd
         else{
            // printing 'b' first as it is odd
            System.out.println(b);
            // printing 'a' second as it is odd
            System.out.println(a);
         }

     }
}
