class Solution{
    public void evenOdd(int a, int b){
        // check if a is even or odd
        if(a % 2 == 0)
            // print a and b in separate lines if a is even
            System.out.print(a + "\n" + b + "\n");
        else
            // print b and a in separate lines if a is odd
            System.out.print(b + "\n" + a + "\n");
    }
}

