    # Function to print the even number first if a is even, or print the odd number first if a is odd.
    def evenOdd(self, a, b):
        if(a % 2 == 0):
            print(a) # Print the even number first
            print(b) # Print the odd number second
        else:
            print(b) # Print the odd number first
            print(a) # Print the even number second

