//Back-end complete function Template for Java


class Geeks{
    
    static int countSumSubsets(int arr[],int n)
    {
        // There are totoal 2^n subsets 
	long  total = 1<<n; 

	// Consider all numbers from 0 to 2^n - 1 
	int count = 0;
	for (long  i=1; i<total; i++) 
	{ 
    		long  sum = 0; 

    		for (int j=0; j<n; j++){
    		    if ((i & (1<<j))!=0) {
    			    sum += arr[j];
    			}
    		}
            if(sum % 2 == 0 ){
                count++;
            }
    	}
    	return count;
        
    }
}
