string ExtractMessage(string s)
{
    // to store resultant string
    string res="";
    // variable to let us know when to add space and when not
    bool space = false;
    // traverse whole string from left to right
    for(int i = 0 ;i<s.length();i++)
    {
        if( i<s.length() - 2 && s[i]=='L' && s[i + 1]=='I' && s[i + 2]=='E')
        {
            i+=2;
            if(space == true)
                {
                    res+=" ";
                    space = false;
                }
        }
       else
        {
            // if current character is part of secret message
            res+=s[i];
            space = true;
        }

    }

    return res;
}
