class Solution{
public:
    int isFactorial(int N){
        int x = 1;
        int i = 1;
        // loop until x becomes greater than N
        while(x<=N)
        {
            // if x is equal to N then return 1
            if(x == N)
                return 1;
            // calculate factorial of i
            x = x * i;
            // increase i by one
            i++;
        }
        // if x crosses N then N is not a factorial number
        return 0;
    }
};

