//Back-end complete function Template for C++
class Solution {
  public:
    //Function to count the number of perfect squares within a given range.
    long long int countPerfectSquares(long long int L, long long int R) 
    { 
        //returning the count of perfect squares within the range
        return (floor(sqrt(R)) - ceil(sqrt(L)) + 1); 
    } 
};
