cpp.full_func:
class Solution {
  public:
    // Function to calculate factorial of a number.
    int factorial(int n) {
        int ans = 1;
        for (int i = 2; i <= n; i++) {
            ans = ans * (i);
        }
        return ans;
    }
};

java.full_func:
class Solution {
    static int factorial(int N) {
        int ans = 1;
        for (int i = 2; i <= N; i++) ans = ans * i;

        return ans;
    }
}

py.full_func:
class Solution:

    def factorial(self, n):
        ans = 1
        # Calculating the factorial
        for i in range(2, n + 1):
            ans = ans * i
        return ans

