// Function to count the frequency of each character in the string 
// using an array of size 27
// (26 for alphabets and 1 for null character)

string solve(string a) {
    int i;
    int b[27];
    for (i = 0; i < 26; i++) b[i] = 0;

    // Variable initialization
    int k = 0, j = 0, c = 0;

    // Loop to count frequency of each character
    for (i = 0; i < a.length(); i++) {
        int d = (int)(a[i] - 'a');
        b[d]++;
    }

    // Loop to count the number of characters that are not a, e, i, o, or u
    for (i = 0; i < 26; i++) {
        if (b[i] > 0 && (i != 0 && i != 4 && i != 8 && i != 14 && i != 20)) c++;
    }

    // Check if the count is even or odd and return the corresponding result
    if (c % 2 == 0) {
        return "SHE!";
    } else
        return "HE!";
}
