#include <iostream>
using namespace std;

int main() {
    int n;
    cin >> n;
    int fib = 0;
    if (n == 0 || n == 1) {
        fib = n;
    } else {
        int a = 0, b = 1, c = 1;

        for (int i = 2; i <= n; i++) {
            c = a + b;
            a = b;
            b = c;
        }
        fib = c;
    }
    cout << fib << endl;
    return 0;
}

