//Back-end complete function Template for C

int fibonacci(int n){
    n--;

    if (n == 0 || n == 1)
        return 1;
    int a = 1, b = 1, c = 1;

    for (int i = 2; i <= n; i++) {
        c = a + b;
        a = b;
        b = c;
    }
    return c;
}

