int distinct(int arr[], int n) {
    // for (int i = 0; i < n; i++)
    //     printf("%d ", arr[i]);
    // printf("%d", n);
    int count = 0;
    bool visited[100000] = {0}; // Assuming elements are within a manageable range

    for (int i = 0; i < n; i++) {
        if (!visited[arr[i] + 1001]) { // If the element hasn't been counted yet
            visited[arr[i] + 1001] = true;
            count++;
        }
    }

    return count;
}

