// Back-end complete function Template for C

int distinct(int arr[], int n) {
    int res = 1;

    // Pick all elements one by one
    for (int i = 1; i < n; i++) {
        int j = 0;
        for (j = 0; j < i; j++)
            if (arr[i] == arr[j]) break;

        if (i == j) res++;
    }
    return res;
}

