// Solution class to find duplicates
class Solution {
  public:
    // Function to find duplicates in the given array
    vector<int> findDuplicates(vector<int>& arr) {
        unordered_map<int, int>
            mp; // Create a hashmap to count occurrences of each element

        // Count each element's occurrence in the array
        for (auto k : arr)
            mp[k]++;

        vector<int> ans; // To store the result

        // Traverse the hashmap and check for elements occurring more than once
        for (auto k : mp) {
            if (k.second > 1) {
                ans.push_back(k.first); // Add the element to the result
            }
        }

        return ans; // Return the list of duplicates or an empty vector if none found
    }
};

