class Solution {
  public:

    int Modulo(int a, string b)
    {
        // Initialize result
        int mod = 0;

        // calculating mod of b with a to make
        // b like 0 <= b < a
        for (int i=0; i<b.length(); i++)
            mod = (mod*10 + b[i] - '0')%a;

        return mod; // return modulo
    }

    int getLastDigit(string a, string b) {

        int len_a = a.length(), len_b = b.length();


        // if exponent is 0
        if (len_b==1 && b[0]=='0' )
            return 1;

        // if base is 0
        if (len_a==1 && a[0]=='0')
            return 0;

        // if exponent is divisible by 4 that means last
        // digit will be pow(a, 4) % 10.
        // if exponent is not divisible by 4 that means last
        // digit will be pow(a, b%4) % 10
        int exp = (Modulo(4,b)==0)? 4 : Modulo(4, b);

        // Find last digit in 'a' and compute its exponent
        int res = pow(a[len_a - 1] - '0', exp);

        // Return last digit of result
        return res % 10;
    }
};

class Solution
{
    int Modulo(int a, string b)
    {
        int mod = 0;
        for (int i = 0; i < b.Length; i++)
            mod = (mod * 10 + b[i] - '0') % a;
        return mod;
    }

    public int getLastDigit(string a, string b)
    {
        int len_a = a.Length;
        int len_b = b.Length;

        if (len_b == 1 && b[0] == '0')
            return 1;

        if (len_a == 1 && a[0] == '0')
            return 0;

        int exp = (Modulo(4, b) == 0) ? 4 : Modulo(4, b);

        int res = 1;
        int baseNum = a[len_a - 1] - '0';
        for (int i = 0; i < exp; i++)
        {
            res *= baseNum;
            res %= 10;
        }

        return res;
    }
}

class Solution {
  getLastDigit(a, b) {
    const Modulo = (a, b) => {
      let mod = 0;
      for (let i = 0; i < b.length; i++) {
        mod = (mod * 10 + parseInt(b[i])) % a;
      }
      return mod;
    }

    const len_a = a.length;
    const len_b = b.length;

    if (len_b === 1 && b[0] === '0') {
      return 1;
    }

    if (len_a === 1 && a[0] === '0') {
      return 0;
    }

    const exp = Modulo(4, b) === 0 ? 4 : Modulo(4, b);
    const res = Math.pow(parseInt(a[len_a - 1]), exp);
    return res % 10;
  }
}
