class Solution {
  public:
    // Function to count the number of occurrences of a digit in a number
    int find(int a, int k) {
        int s = 0;
        if (a == k) // If the number is equal to the digit, increment the count
            return 1;
        while (a != 0) {    // Iterate until the number becomes 0
            int n = a % 10; // Extract the rightmost digit
            if (n ==
                k) // If the digit is equal to the target digit, increment the count
                s++;
            a /= 10; // Remove the rightmost digit
        }
        return s; // Return the count of occurrences
    }

    // Function to count the number of occurrences of a digit in an array of numbers
    int num(int k, vector<int>& arr) {
        int count = 0;
        int n = arr.size();
        for (int i = 0; i < n; i++) { // Iterate over each number in the array
            count +=
                find(arr[i], k); // Call the find function to count occurrences of digit
                                 // in each number and add it to the total count
        }
        return count; // Return the total count of occurrences
    }
};

class Solution {
    // Function to count the number of occurrences of k in a number a.
    public static int find(int a, int k) {
        int s = 0;
        if (a == k) {
            return 1;
        }
        // Loop to check each digit of the number a.
        while (a != 0) {
            int n = a % 10;
            // Incrementing count s if digit n is equal to k.
            if (n == k) {
                s++;
            }
            a /= 10;
        }
        // Returning the count of occurrences of k in number a.
        return s;
    }

    // Function to count the number of occurrences of k in all the numbers in the array
    // a.
    public static int num(int k, int arr[]) {
        int count = 0;
        int n = arr.length;
        // Loop to iterate through each number in the array a.
        for (int i = 0; i < n; i++) {
            // Calling the find function to count the occurrences of k in number a[i]
            count += find(arr[i], k);
        }
        // Returning the total count of occurrences of k in the array a.
        return count;
    }
}

