class Solution {
  public:
    bool findPair(vector<int> &arr, int x) {
        int n = arr.size();
        sort(arr.begin(), arr.end());

        int i = 0;
        int j = 1;

        while (i < n && j < n) {
            if (i != j && arr[j] - arr[i] == x) {
                return true;
            } else if (arr[j] - arr[i] < x) {
                j++;
            } else {
                i++;
            }
        }

        return false;
    }
};

class Solution {
    public boolean findPair(int[] arr, int x) {
        Arrays.sort(arr);
        int i = 0;
        int j = 1;
        int n = arr.length;

        while (i < n && j < n) {
            if (i != j && arr[j] - arr[i] == x) {
                return true;
            } else if (arr[j] - arr[i] < x) {
                j++;
            } else {
                i++;
            }
        }

        return false;
    }
}

