class Solution {
    findPair(arr, x) {
        let n = arr.length;
        arr.sort((a, b) => a - b);

        let i = 0;
        let j = 1;

        while (i < n && j < n) {
            if (i !== j && arr[j] - arr[i] === x) {
                return true;
            } else if (arr[j] - arr[i] < x) {
                j++;
            } else {
                i++;
            }
        }

        return false;
    }
}
