// Back-end complete function Template for C++

class Solution{
public:
    // Function to calculate the greatest common divisor of two numbers.
    int gcd(int a, int b){
        if(a == 0)
            return b;
        return gcd(b%a, a);
    }

    // Function to find the values of x and y such that ax + by = gcd(a, b).
    vector<int> findXY(int a, int b){
        vector<int> v;
        int x = gcd(a, b);

        // Calculating the values of x and y.
        v.emplace_back(b/x);
        v.emplace_back(a/x);
        return v;
    }
};

//User function Template for C#

class Solution
{
    //Complete this function
    public List<int> findXY(int a, int b)
    {
        List<int> v = new List<int>();
        int x = gcd(a, b);

        // Calculating the values of x and y.
        v.Add(b/x);
        v.Add(a/x);
        return v;
    }

    // Function to calculate the greatest common divisor of two numbers.
    public int gcd(int a, int b)
    {
        if(a == 0)
            return b;
        return gcd(b%a, a);
    }
}
