//Back-end complete function Template for C++
/*Function to find frequency of x
 * x : element whose frequency is to be found
 * arr : input vector
 */
class Solution {
  public:
    int findFrequency(vector<int> arr, int x) {
        int count = 0;
        for (auto t : arr) {
            count += (t == x);
        }
        return count;
    }
};

class Solution {
    int findFrequency(int arr[], int x) {
        int count = 0;
        for (Integer t : arr) {
            count += (t == x ? 1 : 0);
        }
        return count;
    }
}

// User function Template for javascript

/**
 * @param {number[]} arr
 * @param {number} x
 * @returns { number}
 */

class Solution {
    findFrequency(arr, x) {
        // code here
        arr.sort((a, b) => a - b);

        let lo = 0, hi = arr.length - 1;

        while (lo <= hi) {
            let mid = Math.floor((lo + hi) / 2);

            if (arr[mid] == x) {
                let freq = 1;

                let left = mid - 1;
                while (left >= 0 && arr[left] == x) {
                    freq++;
                    left--;
                }

                let right = mid + 1;
                while (right < arr.length && arr[right] == x) {
                    freq++;
                    right++;
                }

                return freq;
            }

            if (arr[mid] < x) {
                lo = mid + 1;
            } else {
                hi = mid - 1;
            }
        }

        return 0;
    }
}
