// Back-end complete function Template for C++

class Solution{
public:
    // Function to calculate the last digit
    int lastDigit(long long A, long long B){
        int last = 1;
        // If A and B are equal, the last digit will always be 1
        if(A == B)
            return last;
        // If the difference between B and A is greater than or equal to 5,
        // the last digit will always be 0
        else if (B - A >= 5)
            return 0;
        else{
            // Looping through the numbers from A+1 to B and updating the last digit
            for(long long i = A+1; i <= B; i++)
                last = (last * (i % 10)) % 10;
            return last;
        }
    }
};

