// Back-end complete function Template for C++

class Solution{
public:
    // Function to find the minimum time
    int minTime(int S1, int S2, int N){
        int a = S1, b = S2;

        // Sort the speeds
        if(a > b)
            swap(a, b);

        int low = 0, high = N, mid, mini = b*N;

        // Binary search to find the minimum time
        while(low <= high){
            mid = (low + high)/2;

            // Calculate the time taken based on the speeds
            mini = min(mini , max(mid*a , (N - mid)*b));

            // Adjust the search range based on the time taken
            if(mid*a <= (N - mid)*b)
                low = mid + 1;
            else
                high = mid - 1;
        }

        // Return the minimum time
        return mini;
    }
};
