//Back-end complete function Template for C++

class Solution{   
public:
    //Function to calculate the n-th number in the sequence.
    long long int get(long long int n){
        n--;
        long long int i = 1;

        //Iterating until n is greater than or equal to i.
        while(n >= i){
            n -= i;
            i++;
        } 

        //Returning the n-th number in the sequence.
        return n + 1;
    }
};

