// Returns true if d is present as digit
// in number x.
bool isDigitPresent(int x, int d) {
    // Breal loop if d is present as digit
    while (x > 0) {
        if (x % 10 == d) break;

        x = x / 10;
    }

    // If loop broke
    return (x > 0);
}

// function to display the values
vector<int> printNumbers(int n, int d) {
    vector<int> ret;
    // Check all numbers one by one
    for (int i = 0; i <= n; i++)

        // checking for digit
        if (i == d || isDigitPresent(i, d)) ret.push_back(i);
    return ret;
}

// Driver code

vector<int> solve(int n, int d) {
    vector<int> ret;
    if (n < d) {
        ret.push_back(-1);
        return ret;
    }
    ret = printNumbers(n, d);
}
